public:
    int minDominoRotations(vector<int> &tops, vector<int> &bottoms)
    {
        int n = tops.size();
        // [2,1,2,4,2,2], B = [5,2,6,2,3,2]

        int top = tops[0];
        int bot = bottoms[0];
        int result_top_t = 0;
        int result_top_b = 1;
        int result_bot_t = 1;
        int result_bot_b = 0;
        for (int i = 1; i < n; i++)
        {
            if (tops[i] != top && bottoms[i] != top)
            {
                result_top_b = -1;
                result_top_t = -1;
            }
            else if (tops[i] == top && bottoms[i] == top)
            {
                // 不用操作
            }
            else if (tops[i] == top)
            {
                result_top_b >= 0 ? result_top_b++ : -1;
            }
            else if (bottoms[i] == top)
            {
                result_top_t >= 0 ? result_top_t++ : -1;
            }

            if (tops[i] != bot && bottoms[i] != bot)
            {
                result_bot_b = -1;
                result_bot_t = -1;
            }
            else if (tops[i] == bot && bottoms[i] == bot)
            {
            }
            else if (tops[i] == bot)
            {
                result_bot_b >= 0 ? result_bot_b++ : -1;
            }
            else if (bottoms[i] == bot)
            {
                result_bot_t >= 0 ? result_bot_t++ : -1;
            }

            // std::cout << "---" << i << "|" << tops[i] << "|" << bottoms[i] << "|" << result_top_t << ":" << result_top_b << ":" << result_bot_t << ":" << result_bot_b;
        }
        int result = -1;
        // std::cout << result_top_t << ":" << result_top_b << ":" << result_bot_t << ":" << result_bot_b << std::endl;
        result_top_t >= 0 ? (result == -1 ? (result = result_top_t) : (result = min(result, result_top_t))) : 0;
        result_top_b >= 0 ? (result == -1 ? (result = result_top_b) : (result = min(result, result_top_b))) : 0;
        result_bot_t >= 0 ? (result == -1 ? (result = result_bot_t) : (result = min(result, result_bot_t))) : 0;
        result_bot_b >= 0 ? (result == -1 ? (result = result_bot_b) : (result = min(result, result_bot_b))) : 0;
        return result;
    }
